mapper.select:
SELECT u_id, u_name 
FROM users
WHERE u_id=#{param1} AND u_pw=#{param2}

SELECT u_id, u_name, u_email
FROM USERS
WHERE u_name=#{param1} AND u_email=#{param2}

SELECT u_id, u_name, u_email, u_password
FROM USERS
WHERE u_id=#{param1} AND u_email=#{param2}

SELECT u_id, u_pw, u_name, u_email, u_phone, u_birthdate, u_gender
FROM USERS
WHERE u_id=#{u_id}

mapper.update:
UPDATE USERS 
SET u_name=#{u_name}, u_email=#{u_email}, u_pw=#{u_pw}, u_phone=#{u_phone}
WHERE u_id=#{u_id}
